import React,{useContext} from 'react';
import {Link} from 'react-router-dom';
import {Grid, Paper}from '@material-ui/core';
import {PropertyContext} from '../context/context';
import { makeStyles } from '@material-ui/core/styles';
import ButtonBase from '@material-ui/core/ButtonBase';
import img1 from "../images/room-1.jpeg";
import img2 from "../images/room-2.jpeg";
import NumberFormat from 'react-number-format';
import bed from '../images/Vector.svg';
import bath from '../images/Bath.svg';
import hold from '../images/hold.svg';
import brand from '../images/brand.svg';
import Email from '../images/email.png';
import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext, Dot } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';

const useStyles = makeStyles((theme) => ({
    Head:{
        color: "#F9F3FA",
        fontWeight:300,
        margin:20
    },
    Paper:{
        background:"#1C191D",
        borderRadius: 15,
        width:"100%",
        margin: "30px 0px",
        [theme.breakpoints.down('sm')]: {
          maxWidth:430
        },
    },
    cardContainer:{
        overflow:"auto",
        height:"calc(100vh - 16rem)",
        padding: "0px 25px 25px"
    },
    imageBody:{
        height:440,
        borderRadius:20,
    },
    ButtonBack:{
        color: "#CA18AA",
        position: "absolute",
        top: "47%",
        left: "39%"
    },
    ButtonNext:{
        color: "#CA18AA",
        position: "absolute",
        top: "47%",
        right: "10%"
    }
  }));

export default function SinglePage() {
    const classes = useStyles();
    const context = useContext(PropertyContext)

    return (
        <Paper className={classes.Paper} >
            <h3 className={classes.Head}>15 Search result</h3>
            <div className={classes.cardContainer}>
            <CarouselProvider style={{position:"relative"}}
            totalSlides={3}
                >
                <Slider className={classes.imageBody}>
                    <Slide index={0}><img src={img1} /></Slide>
                    <Slide index={1}><img src={img2} /></Slide>
                    <Slide index={2}><img src={img1} /></Slide>
                </Slider>
                <ButtonBack className={classes.ButtonBack}>{"<"}</ButtonBack>
                <ButtonNext className={classes.ButtonNext}>{">"}</ButtonNext>
                <Dot />
            </CarouselProvider>
            </div>
        </Paper>
    )
}
