import React,{useContext} from 'react';
import {
    Grid, 
    Card, 
    InputBase,
    Select,
    FormControl,
    MenuItem,
    Button,
    InputLabel }from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import {PropertyContext} from '../context/context';

const useStyles = makeStyles((theme) => ({
    LeftBar:{
        background:"#201B20",
        height:"100vh",
        padding:16
    },
    Heading:{
        fontFamily:"roboto",
        fontSize: 18,
        fontWeight: 400,
        letterSpacing:" 0em",
        color:"#F9F3FA"
    },
    selectBody:{
        background:"#0C0B0B",
        padding:16,
        borderRadius:15,
        margin:"10px 0px"
    },
    labelText:{
        fontSize: 12,
        fontWeight: 'bold',
        color:"#ca18aa",
    },
    inputInput: {
        fontFamily:"roboto",
        fontSize: 14,
        fontWeight: 500,
        letterSpacing:" 0em",
        color:"#B8AFB6",
        // [theme.breakpoints.up('md')]: {
        //   width: '20ch',
        // },
      },
    select: {
        maxWidth: 100,
        fontSize:"0.9rem",
        borderStyle:'none',
        color:"#F9F3FA",
        padding:0,
        paddingTop:5,
        // "&:focus":{
        //   borderRadius: 4,
        //   background:"#0C0B0B",
        //   borderColor:"#0C0B0B",
        //   color:"#F9F3FA",
        // },
      },
      icon:{
        right: 12,
        position: 'absolute',
        userSelect: 'none',
        color:"#F9F3FA",
        pointerEvents: 'none'
      },
      paper: {
        borderRadius: 15,
        marginTop: 20,
        maxHeight:300,
        background:'#0C0B0B'
      },
      list: {
        paddingTop:0,
        paddingBottom:0,
        fontFamily:"1rem",
        background:'#0C0B0B',
        "& li":{
          fontWeight:200,
          color:'#F9F3FA',
          paddingTop:12,
          paddingBottom:12,
          background: "#0C0B0B"
        },
        "& li:hover":{
          background: "#0C0B0B",
        },
        "& li.Mui-selected":{
          color:'#F9F3FA',
          fontWeight:200,
          fontFamily:"1rem",
          color:'#F9F3FA',
          background: "#0C0B0B"
        },
        "& li.Mui-selected:hover":{
          background: "#0C0B0B",
          color:'#F9F3FA',
        },
    },
    Button:{
        background:"#D41553",
        borderRadius:10,
        color:"#F9F3FA",
        padding:"20px 0px",
        width:"100%",
        margin: "10px 0px",
        "&:hover":{
            background:"#D41553",
            borderRadius:10,
            color:"#F9F3FA",
          },
    }
  }));
export default function Filter() {
    const classes = useStyles();
    const context = useContext(PropertyContext)
    const[minPrice, setMinPrice]=React.useState('hi')
    const iconComponent = (props) => {
        return (
            <ExpandMoreIcon className={props.className + " " + classes.icon}/>
            )};

    const menuProps = {
        classes: {
            paper: classes.paper,
            list: classes.list
        },
        anchorOrigin: {
            vertical: "bottom",
            horizontal: "left"
        },
        transformOrigin: {
            vertical: "top",
            horizontal: "left"
        },
        getContentAnchorEl: null
    };


    const handleChange = (event) => {
        setMinPrice(event.target.value)
    };


    return (
        <div>
            <Card className={classes.LeftBar}>
                <div className={classes.text}>
                    <h2 className={classes.Heading}>Find a rental property</h2>
                    <Card className={classes.selectBody}>
                        <InputLabel id="demo-mutiple-chip-label" className={classes.labelText}>Search area</InputLabel>
                        <InputBase
                            placeholder="eg. Oxford or NW3"
                            classes={{
                            input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </Card>
                    <Grid container spacing={2}>
                        <Grid item xs>
                            <Card className={classes.selectBody}>
                                <InputLabel id="demo-mutiple-chip-label" className={classes.labelText}>Minimum beds</InputLabel>
                                <FormControl >
                                    <Select
                                        SelectDisplayProps={{ style: { minWidth: 100 } }}
                                        id="demo-mutiple-chip"
                                        value={"all"}
                                        // onChange={handleChangeCounty}
                                        classes={{ root: classes.select }}
                                        MenuProps={menuProps}
                                        disableUnderline
                                        IconComponent={iconComponent}
                                        >
                                        <MenuItem  value="All"  >
                                        All
                                        </MenuItem>
                                        {context.initialData.map((item, i) => (
                                            <MenuItem key={i}  >
                                            {item.beds}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </FormControl>
                            </Card>
                        </Grid>
                        <Grid item xs>
                            <Card className={classes.selectBody}>
                                <InputLabel id="demo-mutiple-chip-label" className={classes.labelText}>Maximum beds</InputLabel>
                                <FormControl >
                                    <Select
                                        SelectDisplayProps={{ style: { minWidth: 100 } }}
                                        id="demo-mutiple-chip"
                                        value={minPrice }
                                        onChange={handleChange}
                                        classes={{ root: classes.select }}
                                        MenuProps={menuProps}
                                        disableUnderline
                                        IconComponent={iconComponent}
                                        >
                                        <MenuItem  value="All"  >
                                        hi
                                        </MenuItem>
                                        {/* {CountyData.map((name, i) => (
                                            <MenuItem key={i} value={name} >
                                            {name}
                                            </MenuItem>
                                        ))} */}
                                    </Select>
                                </FormControl>
                            </Card>
                        </Grid>
                    </Grid>
                    
                    <Card className={classes.selectBody}>
                        <InputLabel id="demo-mutiple-chip-label" className={classes.labelText}>Price per</InputLabel>
                        <FormControl style={{width:"100%"}} >
                            <Select
                                SelectDisplayProps={{ style: { minWidth: "100%" } }}
                                id="demo-mutiple-chip"
                                value={context.Price }
                                // onChange={handleChangeCounty}
                                classes={{ root: classes.select }}
                                MenuProps={menuProps}
                                disableUnderline
                                IconComponent={iconComponent}
                                >
                                <MenuItem  value="All"  >
                                All
                                </MenuItem>
                                {/* {CountyData.map((name, i) => (
                                    <MenuItem key={i} value={name} >
                                    {name}
                                    </MenuItem>
                                ))} */}
                            </Select>
                        </FormControl>
                    </Card>
                    <Grid container spacing={2}>
                        <Grid item xs>
                            <Card className={classes.selectBody}>
                                <InputLabel id="demo-mutiple-chip-label" className={classes.labelText}>Minimum price</InputLabel>
                                <FormControl >
                                    <Select
                                        SelectDisplayProps={{ style: { minWidth: 100 } }}
                                        id="demo-mutiple-chip"
                                        value={context.minPrice}
                                        // onChange={handleChangeCounty}
                                        classes={{ root: classes.select }}
                                        MenuProps={menuProps}
                                        disableUnderline
                                        IconComponent={iconComponent}
                                        >
                                        <MenuItem  value="All"  >
                                        All
                                        </MenuItem>
                                        {/* {CountyData.map((name, i) => (
                                            <MenuItem key={i} value={name} >
                                            {name}
                                            </MenuItem>
                                        ))} */}
                                    </Select>
                                </FormControl>
                            </Card>
                        </Grid>
                        <Grid item xs>
                            <Card className={classes.selectBody}>
                                <InputLabel id="demo-mutiple-chip-label" className={classes.labelText}>Maximum price</InputLabel>
                                <FormControl >
                                    <Select
                                        SelectDisplayProps={{ style: { minWidth: 100 } }}
                                        id="demo-mutiple-chip"
                                        value={context.maxPrice }
                                        // onChange={handleChangeCounty}
                                        classes={{ root: classes.select }}
                                        MenuProps={menuProps}
                                        disableUnderline
                                        IconComponent={iconComponent}
                                        >
                                        <MenuItem  value="All"  >
                                        All
                                        </MenuItem>
                                        {/* {CountyData.map((name, i) => (
                                            <MenuItem key={i} value={name} >
                                            {name}
                                            </MenuItem>
                                        ))} */}
                                    </Select>
                                </FormControl>
                            </Card>
                        </Grid>
                    </Grid>
                    <Button className={classes.Button}>Find Properties</Button>
                </div>
            </Card>
        </div>
    )
}
