import React,{useContext} from 'react';
import {Link} from 'react-router-dom';
import {Grid, Paper}from '@material-ui/core';
import {PropertyContext} from '../context/context';
import { makeStyles } from '@material-ui/core/styles';
import ButtonBase from '@material-ui/core/ButtonBase';
import img1 from "../images/room-1.jpeg";
import img2 from "../images/room-2.jpeg";
import './Slider.css'
import NumberFormat from 'react-number-format';
import bed from '../images/Vector.svg';
import bath from '../images/Bath.svg';
import hold from '../images/hold.svg';
import brand from '../images/brand.svg';
import Email from '../images/email.png';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext, Dot, DotGroup } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';

const useStyles = makeStyles((theme) => ({
    Head:{
        color: "#F9F3FA",
        fontWeight:300,
        margin:20
    },
    Paper:{
        background:"#1C191D",
        borderRadius: 15,
        width:"100%",
        margin: "30px 0px",
        [theme.breakpoints.down('sm')]: {
          maxWidth:430
        },
    },
    cardContainer:{
        overflow:"auto",
        height:"calc(100vh - 16rem)",
        padding: "0px 25px 25px"
    },
    imageBody:{
        height:450,
        borderRadius:20,
    },
    ButtonBack:{
        color: "#CA18AA",
        position: "absolute",
        top: "45%",
        left: "5%",
        background:"none",
        border:0
    },
    ButtonNext:{
        color: "#CA18AA",
        position: "absolute",
        top: "45%",
        right: "5%",
        background:"none",
        border:0
    },
    Arrow:{
        height:"3rem",
        width:"3rem"
    },
    Dot:{
        margin:30
    },
    DotGroup:{
        textAlign:'center'
    },
    Email:{
        marginLeft: 'auto',
        height: 28,
        width:36
    },
    Address:{
        color:"#F9F3FA",
        fontWeight: 500,
        fontSize:"1.1rem",
        letterSpacing:"0.04em"
    },
    Price:{
        display:"flex",
        color: "#F9F3FA",
        fontWeight:500,
        letterSpacing:"0.03em",
        fontSize:"1.2rem",
        margin:"16px 0px"
    },
    svgText:{
        color:"#B8AFB6",
        margin:"0px 10px",
        fontSize:14
    },
    svgContainer:{
        display:"flex",
        alignItems:"center",
        margin:"10px 0px"
    },
    brand:{
        position:"relative"
    },
    centered: {
        position:" absolute",
        fontSize:3,
        top: "30%",
        background:"#0019A8",
        color:"white",
        left: "50%",
        transform: "translate(-50%, -50%)"
    },
    movein:{
        fontSize:12,
        color:"#B8AFB6",
        margin:"0px"
    }
  }));

export default function SinglePage() {
    const classes = useStyles();
    const context = useContext(PropertyContext)

    return (
        <Paper className={classes.Paper} >
            <h3 className={classes.Head}>15 Search result</h3>
            <div className={classes.cardContainer}>
                <div style={{height:200}}>
                    <CarouselProvider style={{position:"relative"}}
                        naturalSlideWidth={100}
                        naturalSlideHeight={125}
                        totalSlides={3}
                        >
                        <Slider className={classes.imageBody} >
                            <Slide index={0}><img src={img1} style={{height:450, width:"100%"}}/></Slide>
                            <Slide index={1}><img src={img2} style={{height:450, width:"100%"}}/></Slide>
                            <Slide index={2}><img src={img1} style={{height:450, width:"100%"}}/></Slide>
                        </Slider>
                        <ButtonBack className={classes.ButtonBack}>
                            <ArrowBackIosIcon className={classes.Arrow}/>
                        </ButtonBack>
                        <ButtonNext className={classes.ButtonNext}>
                            <ArrowForwardIosIcon className={classes.Arrow}/>
                        </ButtonNext>
                        <DotGroup className="prc-dotGroup"/>
                    </CarouselProvider>
                    <div style={{display:'flex', alignItems:'center'}}>
                        <h1 className={classes.Address}>3 Bedroom flat in Hackney Bridge</h1>
                        <img src={Email} alt='email' className={classes.Email}/>
                    </div>
                    <NumberFormat className={classes.Price} value={1420} displayType={'text'} thousandSeparator={true} prefix={'£'} suffix={` pcm`}/>
                    <Grid container>
                        <Grid item xs={12} sm={6} md={3}>
                            <div style={{display:"flex"}}>
                                <div className={classes.svgContainer}>
                                    <img src={hold}  alt="bed" height={"16px"}/>
                                    <p className={classes.svgText}>{3}</p>
                                </div>
                                <div className={classes.svgContainer}>
                                    <img src={bed}  alt="bed" height={"16px"}/>
                                    <p className={classes.svgText}>{3}</p>
                                </div>
                                <div className={classes.svgContainer}>
                                    <img src={bath}  alt="bed" height={"16px"}/>
                                    <p className={classes.svgText}>{3}</p>
                                </div>
                            </div>
                        </Grid>
                        <Grid item xs={12} sm={6} md={3}>
                            <div className={classes.svgContainer}>
                                <div className={classes.brand}>
                                    <img src={brand}  alt="brand" style={{heifght:"22"}}/>
                                    <p className={classes.centered}>UNDERGROUND</p>
                                </div>
                                <p className={classes.svgText} style={{fontSize:12}}>{'South Kensington (0.1 mile)'}</p>
                            </div>
                        </Grid>
                        <Grid item xs={12} sm={6} md={3}>
                            <p className={classes.movein}>{ 'Furnished' }</p>
                        </Grid>
                        <Grid item xs={12} sm={6} md={3}>
                            <p className={classes.movein}>{ 'Move-in from 20/04/21' }</p>
                        </Grid>
                    </Grid>
                    
                    
                    
                    
                    
                    {/* <div style={{display:"flex"}}>
                        <div style={{display:"flex"}}>
                            <div className={classes.svgContainer}>
                                <img src={hold}  alt="bed" height={"16px"}/>
                                <p className={classes.svgText}>{3}</p>
                            </div>
                            <div className={classes.svgContainer}>
                                <img src={bed}  alt="bed" height={"16px"}/>
                                <p className={classes.svgText}>{3}</p>
                            </div>
                            <div className={classes.svgContainer}>
                                <img src={bath}  alt="bed" height={"16px"}/>
                                <p className={classes.svgText}>{3}</p>
                            </div>
                        </div>
                        <div style={{display:"flex"}}>
                            <div className={classes.svgContainer}>
                                <div className={classes.brand}>
                                    <img src={brand}  alt="brand" style={{heifght:"22"}}/>
                                    <p className={classes.centered}>UNDERGROUND</p>
                                </div>
                                <p className={classes.svgText} style={{fontSize:12}}>{'South Kensington (0.1 mile)'}</p>
                            </div>
                                
                        </div>
                                <p className={classes.movein}>Move-in from { 'Furnished' }</p>
                                <p className={classes.movein}>Move-in from { 'Move-in from 20/04/21' }</p>
                    </div> */}
                </div>
            </div>
        </Paper>
    )
}
