import React, { Component } from 'react';
import items from '../data';

const PropertyContext = React.createContext();

export default class PropertyProvider extends Component {
    state ={
        initialData: [],
        sortedData:[],
    };

    componentDidMount(){
      let initialData =  items
      this.setState({
          initialData
      })
    }
 
    render() {
        console.log("data", this.state.initialData)
        return (
            <PropertyContext.Provider value = {{...this.state}} >
                {this.props.children}
            </PropertyContext.Provider>
        )
    }
}

const PropertyConsumer = PropertyContext.Consumer;

export { PropertyProvider, PropertyConsumer, PropertyContext }
