import React,{useContext} from 'react';
import {Link} from 'react-router-dom';
import {Grid, Card, Paper, Container }from '@material-ui/core';
import Typography from '@material-ui/core/Typography';
import {PropertyContext} from '../context/context';
import { makeStyles } from '@material-ui/core/styles';
import ButtonBase from '@material-ui/core/ButtonBase';
import img1 from "../images/room-1.jpeg";
import NumberFormat from 'react-number-format';
import bed from '../images/Vector.svg';
import bath from '../images/Bath.svg';
import hold from '../images/hold.svg';
import brand from '../images/brand.svg';
import brandTitle from '../images/brandTitle.png'

const useStyles = makeStyles((theme) => ({
    Head:{
        color: "#F9F3FA"
    },
    Paper:{
        background:"#1C191D",
        borderRadius: 15,
        padding: 8,
        margin: "30px 0px",
        height:"calc(100vh - 10rem)",
        
        [theme.breakpoints.down('sm')]: {
          maxWidth:360
        },
    },
    cardContainer:{

    },
    CardBody:{
        padding: 16,
        borderRadius:15,
        background:" #18121A"
    },
    Image: {
        height: 200,
        borderRadius: 20
    },
    img: {
        height:'100%',
        borderRadius: 20,
        width:"100%",
        margin: 'auto',
        display: 'block',
        maxWidth: '100%',
        maxHeight: '100%',
    },
    Address:{
        color:"#F9F3FA",
        fontWeight: 500,
        fontSize:"1rem",
        letterSpacing:"0.04em"
    },
    Price:{
        display:"flex",
        color: "#F9F3FA",
        fontWeight:500,
        letterSpacing:"0.03em",
        fontSize:"1.2rem",
        margin:"16px 0px"
    },
    svgText:{
        color:"#B8AFB6",
        margin:"0px 10px",
        fontSize:14
    },
    svgContainer:{
        display:"flex",
        alignItems:"center",
        margin:"10px 0px"
    },
    brand:{
        position:"relative",
    },
    centered: {
        position:" absolute",
        fontSize:3,
        top: "30%",
        background:"#0019A8",
        color:"white",
        left: "50%",
        transform: "translate(-50%, -50%)"
    },
    movein:{
        fontSize:12,
        color:"#B8AFB6",
        margin:"0px"
    }
  }));

export default function PropertyList() {
    const classes = useStyles();
    const context = useContext(PropertyContext)
    const images = context.initialData.map(card => card.images)

    return (
        <Paper className={classes.Paper} >
            <h3 className={classes.Head}>15 Search result</h3>
            <div className={classes.cardContainer}>
            <Grid container spacing={5}>
                {context.initialData.map((card)=>(
                    <Grid item key={card.id} xs={12}  >
                        <Paper className={classes.CardBody} elevation={5}>
                            <Grid container spacing={2} >
                                <Grid item xs={12} sm={12} md={6} container style={{maxWidth:340}}>
                                    <Link  to={`/DetailPage/${card.id}`} >
                                        <ButtonBase className={classes.Image}>
                                            <img className={classes.img} alt="complex" src={img1} />
                                        </ButtonBase>
                                    </Link>
                                </Grid>
                                <Grid item xs={12} sm={12} md={6} container>
                                    <Grid  container>
                                        <Grid item container direction="column" spacing={2}>
                                            <Grid item >
                                                <h3 component="subtitle1" className={classes.Address}>
                                                    {card.name}
                                                </h3>
                                                <NumberFormat className={classes.Price} value={card.price} displayType={'text'} thousandSeparator={true} prefix={'Â£'} suffix={` pcm`}/>
                                                <div style={{display:"flex"}}>
                                                    <div className={classes.svgContainer}>
                                                        <img src={hold}  alt="bed" height={"16px"}/>
                                                        <p className={classes.svgText}>{card.Beds}</p>
                                                    </div>
                                                    <div className={classes.svgContainer}>
                                                        <img src={bed}  alt="bed" height={"16px"}/>
                                                        <p className={classes.svgText}>{card.Beds}</p>
                                                    </div>
                                                    <div className={classes.svgContainer}>
                                                        <img src={bath}  alt="bed" height={"16px"}/>
                                                        <p className={classes.svgText}>{card.Beds}</p>
                                                    </div>
                                                </div>
                                                <div style={{display:"flex"}}>
                                                    <div className={classes.svgContainer}>
                                                        <div className={classes.brand}>
                                                            <img src={brand}  alt="brand" style={{heifght:"22"}}/>
                                                            <p className={classes.centered}>UNDERGROUND</p>
                                                        </div>
                                                        <p className={classes.svgText} style={{fontSize:12}}>{card.distance}</p>
                                                    </div>
                                                </div>
                                                <p className={classes.movein}>Move-in from {card.moveIn}</p>
                                            </Grid>
                                        </Grid>
                                        <Grid item>
                                            {/* <NumberFormat className={classes.Price} value={card.ListPrice} displayType={'text'} thousandSeparator={true} prefix={'$'} /> */}
                                            {/* {card.CountyOrParish} */}
                                        </Grid>
                                        </Grid>
                                    </Grid>
                                </Grid>
                            </Paper>
                    </Grid>
                ))}
            </Grid>
            </div>
        </Paper>
    )
}
